     setSearchTerm(event. target.value);
    }
    function handleSubmit (event) {
      event.preventDefault();
     console. log('Submitting'); 
    }
    useEffect (( )=> {
      localStorage.setItem('searchTerm', searchTerm);
      }, [searchTerm]);
      useEffect(()=> {
        async function fetchData(){
          try {
            setLoading(true);
